mapper.insert:
INSERT 
 INTO 
 TB_BOARD(
	 BNUM
	,BTITLE
	,BCONTENT
	,MID
	,TEXT
	,THUMIDX
	,BDATE
	)
 VALUES(
 	 #{bnum}
 	,#{btitle}
 	,#{bcontent}
 	,#{userId}
 	,#{text}
 	,#{thumIdx}
 	,TO_CHAR(SYSDATE, 'yyyy-mm-dd hh24:mi:ss')
 	)

mapper.update:
UPDATE 
	TB_BOARD 
SET 
 	 BTITLE=#{btitle}
 	,BCONTENT=#{bcontent}
 	,THUMIDX=#{thumIdx}
 	,TEXT=#{text}
WHERE 1 = 1
AND BNUM = #{bnum}
