mapper.insert:
INSERT
	INTO
	TB_REPLY(
		 RNUM
		,BNUM
		,RCONTENT
		,MID
		,RDATE
	)
	VALUES(
		 #{rnum}
		,#{bnum}
		,#{rcontent}
		,#{mid}
		,TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
	)

mapper.update:
UPDATE 
	TB_REPLY
SET
	RCONTENT = #{rcontent}
WHERE 1 = 1
AND RNUM = #{rnum}

