mapper.insert:
INSERT
	INTO
	TB_RREPLY(
		 RRNUM
		,RNUM
		,BNUM
		,RRCONTENT
		,MID
		,RRDATE
	)
	VALUES(
		 #{rrnum}
		,#{rnum}
		,#{bnum}
		,#{rrcontent}
		,#{mid}
		,TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
	)

mapper.update:
UPDATE
	TB_RREPLY
SET
	RRCONTENT = #{rrcontent}
WHERE 1 = 1
AND RRNUM = #{rrnum}
